import React from 'react';
import './Reserve.css';
import { useState } from 'react';
import axios from 'axios';

const Reserve = () => {
    let [data, setdata] = useState({});

    let fun = (e) => {
        if (e.target.type === 'datetime-local') {
            const dateTimeValue = e.target.value;
            const formattedDate = new Date(dateTimeValue).toLocaleDateString();
            const formattedTime = new Date(dateTimeValue).toLocaleTimeString();
            setdata({ ...data, 'date': formattedDate, 'time': formattedTime });
        } else {
            setdata({ ...data, [e.target.name]: e.target.value });
        }
    };
    

    return (
        <div className='reserve-cont'>
            <div className="cont">
                <input type="text" placeholder='Enter Your Name' name='name' onChange={fun} />
                <input type="number" placeholder='Phone Number' name='phone' onChange={fun} />
                <input type="text" placeholder='Email Id' name='email' onChange={fun} />
                <input type="datetime-local" name='time' onChange={fun} />
                <input type="text" placeholder='Address' name='address' onChange={fun} />
            </div>
            <div className="submit-button">
                <button onClick={() => {
                    axios.post("http://localhost:3000/store", data).then((res) => {
                        console.log(res);
                    }).catch((err) => console.log(err))
                }}>Submit</button>
            </div>
        </div>
    )
}

export default Reserve;
